var UIModals = function () {
    var n = function () {
        var draggableElement = $("#draggable");
        if (draggableElement.length) {  // Check if element exists
            draggableElement.draggable({ handle: ".modal-header" });
        }
    };
    return {
        init: function () {
            n();
        }
    };
}();

jQuery(document).ready(function () {
    UIModals.init();
});